import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'
import './Characters.css'

const Characters = ({ pageNumber, nextPage, prevPage, buttonVal}) => {
    let navigate = useNavigate();

    const [character, setCharacters] = useState('');
    const [loading, setLoading] = useState(true)



    useEffect(() => {
        async function fetchCharacters() {

            let res = await axios.get(`https://anapioficeandfire.com/api/characters?page=${pageNumber}&pagesize=10`)
                setCharacters(res.data)
                setLoading(false)
        }

        fetchCharacters();
    }, [pageNumber])



    const navigateToCharacter = (url) => {
        console.log(url)
        try {
            const characterId = url.split('/').pop();
            console.log(characterId)
            navigate(`/character/${characterId}`);

        } catch (e) {
            console.log(e)
        }

    }

    const bs = () => {
        return (
            <>
            <button onClick={buttonVal} value={pageNumber - 4}>{pageNumber - 4}</button>
            <button onClick={buttonVal} value={pageNumber - 3}>{pageNumber - 3}</button>
            <button onClick={buttonVal} value={pageNumber - 2}>{pageNumber - 2}</button>
            <button onClick={buttonVal} value={pageNumber - 1}>{pageNumber - 1}</button>
            </>

        )
    }

    if (loading) {
        return (
            <div>Loading...</div>
        )
    }


    return (

        <div>

            {character.map(data => (
                <div className="character-details" key={data.url}>

                    <button className="character-details"
                        onClick={() => navigateToCharacter(data.url)}>
                        {data.name === "" ? data.aliases : data.name}
                        {data.culture === '' ? '' : `, ${data.culture}`},
                        {data.gender === 'Female' ? ' ♀️' : ' ♂️'}
                    </button>


                </div>


            ))}
            <button onClick={prevPage} >Prev</button>
                {pageNumber >= 5 ? bs() : null}

            <button style={{backgroundcolor: 'yellow'}} onClick={buttonVal} value={pageNumber}>{pageNumber}</button>
            <button onClick={buttonVal} value={pageNumber + 1}>{pageNumber + 1}</button>
            <button onClick={buttonVal} value={pageNumber + 2}>{pageNumber + 2}</button>
            <button onClick={buttonVal} value={pageNumber + 3}>{pageNumber + 3}</button>
            <button onClick={buttonVal} value={pageNumber + 4}>{pageNumber + 4}</button>


            <button onClick={nextPage}>Next</button>



        </div>
    )
}

export default Characters;

