    return (
        <div>
            <CharacterContext.Provider value={() => nextPage}>
            <Characters pageNumber={pageNumber} nextPage={() => nextPage()} character={character} setCharacters={() => setCharacters}/>
            </CharacterContext.Provider>

        </div>
    )
}

export default CharacterFilter;
