import React, { useState, useEffect, useParams, useContext } from 'react';
import axios from 'axios';
import { Link, Navigate, useNavigate } from 'react-router-dom'
import Character from './Character';
import CharacterContext from './CharacterContext';
import './Characters.css'

const Characters = ({ pageNumber, nextPage, character, setCharacters}) => {
    let navigate = useNavigate();
    // const wow = useContext(CharacterContext)

    // const [character, setCharacters] = useState('');
    // const [loading, setLoading] = useState(true)



    useEffect(() => {
        async function fetchCharacters() {

            let res = await axios.get(`https://anapioficeandfire.com/api/characters?page=${pageNumber}&pagesize=10`)
            if (character.length < 10)
                setCharacters(res.data)

        }


        fetchCharacters();
    }, [pageNumber])



    const navigateToCharacter = (url) => {
        console.log(url)
        try {
            const characterId = url.split('/').pop();
            console.log(characterId)
            navigate(`/character/${characterId}`);

        } catch (e) {

        }

    }

    // if (loading) {
    //     return (
    //         <div>Loading...</div>
    //     )
    // }

    // const next = () => {
    //     // setPageNumber(pageNumber + 1)
    //     setLoading(true)
    //     setCharacters('');
    // }
    // const prev = () => {
    //     if (pageNumber === 1) {
    //         // setPageNumber(1)
    //     } else {
    //         // setPageNumber(pageNumber - 1)
    //         setLoading(true)
    //         setCharacters('');
    //     }

    // }




    return (

        <div>

            {character.map(data => (
                <div className="character-details" key={data.url}>

                    <button className="character-details"
                        onClick={() => navigateToCharacter(data.url)}>
                        {data.name === "" ? data.aliases : data.name}
                        {data.culture === '' ? '' : `, ${data.culture}`},
                        {data.gender === 'Female' ? ' ♀️' : ' ♂️'}
                    </button>


                </div>


            ))}
            <button >Prev</button>
            <button onClick={nextPage}>Next</button>



        </div>
    )
}

export default Characters;

