import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Houses from './Houses';
import { useNavigate, useParams } from 'react-router-dom';

const HouseFilter = () => {
  const [pageNumber, setPageNumber] = useState(1);
  const { pageNumber: paramPageNumber } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    // Check if there's a pageNumber parameter in the URL and use it if present
    if (paramPageNumber) {
      setPageNumber(Number(paramPageNumber));
    } else {
      // Default to page 1 if no parameter is specified
      setPageNumber(1); 
    }
  }, [paramPageNumber]);

  const nextPage = () => {
    setPageNumber(pageNumber + 1);
    navigate(`/capstone/houses/page/${pageNumber + 1}`);
  };

  const prevPage = () => {
    if (pageNumber > 1) {
      setPageNumber(pageNumber - 1);
      navigate(`/capstone/houses/page/${pageNumber - 1}`);
    }
  };

  const buttonVal = (e) => {
    const newPageNumber = Number(e.target.value);
    setPageNumber(newPageNumber);
    navigate(`/capstone/houses/page/${newPageNumber}`);
  };

  return (
    <div>
      <Houses
        pageNumber={pageNumber}
        nextPage={nextPage}
        prevPage={prevPage}
        buttonVal={buttonVal}
      />
    </div>
  );
};

export default HouseFilter;
