import React, { useState, useEffect } from "react";
import Characters from "./Characters";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useContext } from "react";
import CharacterContext from "./CharacterContext";

const CharacterFilter = () => {
    const [pageNumber, setPageNumber] = useState(10);
    const [loading, setLoading] = useState(true)
    const [character, setCharacters] = useState('');

    let navigate = useNavigate();
    useEffect(() => {

        const navigateToPage = () => {
            navigate(`/characters/page/${pageNumber}`)
        }

        async function fetchCharacters() {
            try {
                let res = await axios.get(`https://anapioficeandfire.com/api/characters?page=${pageNumber}&pagesize=10`)
                if (character.length < 10)
                    setCharacters(res.data)
                    setLoading(false)
                    setPageNumber(pageNumber)

            } catch (e) {
                console.log(e)
            }
        }
        fetchCharacters();
        navigateToPage()

    }, [pageNumber])



    // const navigateToCharacter = (url) => {
    //     try {
    //         const characterId = url.split('/').pop();
    //         navigate(`/character/${characterId}`);

    //     } catch (e) {
    //         console.log(e)
    //     }

    // }

    if (loading) {
        return (
            <div>Loading...</div>
        )
    }

    const nextPage = () => {
        setPageNumber(pageNumber + 1)
    }

    return (
        <div>
            <CharacterContext.Provider>
            <Characters pageNumber={pageNumber} nextPage={() => nextPage()} character={character} setCharacters={() => setCharacters}/>
            </CharacterContext.Provider>

        </div>
    )
}

export default CharacterFilter;
