import React from "react";
import { useParams } from "react-router-dom";
import { useContext, useEffect, useState} from "react";
import axios from "axios";
const Character = () => {
const {characterId} = useParams();
const [character, setCharacter] = useState('')
const [Loading, setLoading] = useState(true)
const [spouse, setSpouse] = useState('')
    useEffect(() => {
        async function getCharacter(){
            let res = await axios.get(`https://anapioficeandfire.com/api/characters/${characterId}`)
            setCharacter(res.data)
            setLoading(false)
        }
        getCharacter();


    }, [Loading])

    if(Loading){
        return <div>... Loading</div>
    }
return (
    <div>
        <ul>
            <li>
                Name: {character.name === '' ? 'Unknown' : character.name} 
            </li> 
            <li>
                Aliases: {character.aliases[0] === '' ? 'No Aliases' : character.aliases.map(data => (
                    <ul>
                        <li>{data}</li>
                    </ul>
                ))}
            </li>
            <li>
                Gender: {character.gender === '' ? 'Unknown' : character.gender}
            </li>
            <li>
                Culture: {character.culture === '' ? 'Unknown' : character.culture}
            </li>
            <li>
                Born: {character.born === '' ? 'Unknown' : character.born}
            </li>
            <li>
                Died: {character.died === '' ? 'Unknown' : character.died}
            </li>
            <li>
                Titles: {character.titles[0] === '' ? 'Unknown' : character.titles.map(data => (
                    <ul>
                        <li>{data}</li>
                    </ul>
                ))}
            </li>
            <li>
                Father: {character.father}
            </li>
            <li>
                Mother: {character.mother}
            </li>
            <li>
                Spouse: {character.spouse}
            </li>
            <li>
                Allegiances: {character.allegiances === '' ? 'None' : character.allegiances.map(data => (
                    <ul>
                        <li>{data}</li>
                    </ul>
                ))}
            </li>
        </ul>
        {console.log(character)}
    </div>
)

}

export default Character;
