import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Houses = ({ pageNumber, prevPage, nextPage }) => {
    const [houses, setHouses] = useState([]);
    const [pageSize, setPageSize] = useState(10);
    const [loading, setLoading] = useState(true);
    const [currentLordNames, setCurrentLordNames] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchHouses() {
            try {
                let res = await axios.get(`https://anapioficeandfire.com/api/houses?page=${pageNumber}&pagesize=${pageSize}`);
                setHouses(res.data);
                setLoading(false);
            } catch (e) {
                console.error(e);
            }
        }

        fetchHouses();
    }, [pageNumber, pageSize]);

    async function getLordName(currentLordUrl) {
        try {
            if (currentLordUrl) {
                if (currentLordNames[currentLordUrl]) {
                    return currentLordNames[currentLordUrl];
                } else {
                    const res = await axios.get(currentLordUrl);
                    const lordName = res.data.name || "Unknown";
                    setCurrentLordNames((prevState) => ({
                        ...prevState,
                        [currentLordUrl]: lordName,
                    }));
                    return lordName;
                }
            }
            return "Unknown";
        } catch (e) {
            console.error(e);
            return "Unknown";
        }
    }

    if (loading) {
        return <div>...Loading</div>;
    }

    return (
        <div>
            <div className="main-div">
                <h1 className="header">Houses</h1>
                <div className="select-div">
                    <p>Page Size:</p>
                    <select className="selector" onChange={(e) => setPageSize(e.target.value)}>
                        <option>10</option>
                        <option>20</option>
                        <option>30</option>
                        <option>40</option>
                        <option>50</option>
                    </select>
                </div>
                {houses.map((data) => (
                    <div style={{ textAlign: "center", margin: "20px" }} className="character-details" key={data.url}>
                        <div className="character-details">
                            <p>Name: {data.name === "" ? 'Unknown' : data.name}</p>
                            <p>Titles: {data.titles[0] === "" ? "None" : ` ${data.titles}`}</p>
                            {data.currentLord === "" ? "" : (
                                <p>
                                    Current Lord: {getLordName(data.currentLord)}
                                </p>
                            )}
                        </div>
                    </div>
                ))}
            </div>
            <div className="pages-div">
                {pageNumber > 1 ? <button className="buttons" onClick={prevPage}>Prev</button> : null}
                <button className="buttons main-button">{pageNumber}</button>
                <button className="buttons" onClick={nextPage}>Next</button>
            </div>
        </div>
    );
};

export default Houses;
