import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Link, Navigate, useNavigate } from 'react-router-dom'
import Character from './Character';
import CharacterContext from './CharacterContext';
import './Characters.css'

const Characters = () => {

    const [character, setCharacters] = useState('');
    const [loading, setLoading] = useState(true)
    const [headers, setHeaders] = useState([])
    const [fetchData, setFetchData] = useState('https://anapioficeandfire.com/api/characters?page=1&pagesize=10')
    useEffect(() => {
        async function fetchCharacters() {
            let res = await axios.get(fetchData)
            if (character.length < 10)
                setCharacters(res.data)
            setLoading(false)
            setHeaders(res.headers.link.split(','))
        }
        fetchCharacters();
    }, [fetchData])

    const navigate = useNavigate();
    const props = useContext(CharacterContext)

    const navigateToCharacter = ({ data }) => {
        const url = data.url.split('').splice(44).join('')
        props.addData(url)
        console.log(props)
        navigate(`/character/${data.name.replaceAll(' ', '') === '' ? data.aliases[0].replaceAll(' ', '') : data.name.replaceAll(' ', '')}`)


    }

    if (loading) {
        return (
            <div>Loading...</div>
        )
    }

    const prev = () => {
        setFetchData(headers[0])
        console.log(headers[0])
    }




    return (

        <div>

            {character.map(data => (
                <div className="character-details">

                    <button className="character-details"
                        onClick={() => navigateToCharacter({ data })}>
                        {data.name === "" ? data.aliases : data.name}
                        {data.culture === '' ? '' : `, ${data.culture}`},
                        {data.gender === 'Female' ? ' ♀️' : ' ♂️'}
                    </button>

                </div>

            ))}

            <button onClick={() => prev()}>Next</button>

        </div>
    )
}

export default Characters;

