import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Houses = ({ pageNumber, prevPage, nextPage }) => {

    const [houses, setHouses] = useState('')
    const [pageSize, setPageSize] = useState(10)
    const [Loading, setLoading] = useState(true)
    const [lord, setLord] = useState('')
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchHouses() {
            try {
                let res = await axios.get(`https://anapioficeandfire.com/api/houses?page=${pageNumber}&pagesize=${pageSize}`)
                setHouses(res.data)
                setLoading(false)
            } catch (e) {
                console.log(e)
            }



        }

        fetchHouses()
    }, [pageNumber, Loading, pageSize])

    const navigateToHouse = (url) => {
        try {
            const houseId = url.split('/').pop();
            navigate(`/houses/${houseId}`);

        } catch (e) {
            console.log(e)
        }

    }

    async function getCharacter(characterId){
        try {
        let res = await axios.get(characterId)
        setLord(res.data)
        } catch(e){
            console.log(e)
        }
    }

    if (Loading) {
        return (
            <div>
                ...Loading
            </div>
        )
    }
    return (
        <div>

        <div  style={{display: 'inline-block'}}>
        <div style={{display: 'inline-block'}}>
            <select onChange={(e) => setPageSize(e.target.value)}>
                <option>10</option>
                <option>20</option>
                <option>30</option>
                <option>40</option>
                <option>50</option>
            </select>
        </div>
        {houses.map(data => (
            <div style={{textAlign: 'center', margin: '20px'}} className="character-details" key={data.url}>

                <p className="character-details">
                    Name: {data.name === "" ? 'Unknown': data.name}
                    <br/>

                    {data.titles[0] === '' ? '' : `Titles: ${data.titles}`}
                    {/* {getCharacter(data.currentLord)} */}
                    {console.log(data.currentlord)}
                </p>
            </div>
        ))}

        </div>

        <div>
        {pageNumber > 1 ? <button onClick={prevPage} >Prev</button> : null  }


            <button>{pageNumber}</button>
        <button onClick={nextPage}>Next</button>
        </div>

    </div>
    )
}

export default Houses;
