import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom'
import './Characters.css'

const Characters = ({ pageNumber, nextPage, prevPage, buttonVal}) => {
    let navigate = useNavigate();

    const [character, setCharacters] = useState('');
    const [loading, setLoading] = useState(true)
    const [pageSize, setPageSize] = useState(20)



    useEffect(() => {
        async function fetchCharacters() {
            try{
            let res = await axios.get(`https://anapioficeandfire.com/api/characters?page=${pageNumber}&pagesize=${pageSize}`)
                setCharacters(res.data)
                setLoading(false)
            } catch(e){
                console.log(e)
            }
        }

        fetchCharacters();
    }, [pageNumber, loading])



    const navigateToCharacter = (url) => {
        try {
            const characterId = url.split('/').pop();
            navigate(`/character/${characterId}`);

        } catch (e) {
            console.log(e)
        }

    }

    const hiPages = () => {
        return (
            <>
            <button onClick={buttonVal} value={pageNumber - 4}>{pageNumber - 4}</button>
            <button onClick={buttonVal} value={pageNumber - 3}>{pageNumber - 3}</button>
            <button onClick={buttonVal} value={pageNumber - 2}>{pageNumber - 2}</button>
            <button onClick={buttonVal} value={pageNumber - 1}>{pageNumber - 1}</button>
            <button style={{backgroundColor: 'grey'}} onClick={buttonVal} value={pageNumber}>{pageNumber}</button>
            <button onClick={buttonVal} value={pageNumber + 1}>{pageNumber + 1}</button>
            <button onClick={buttonVal} value={pageNumber + 2}>{pageNumber + 2}</button>
            <button onClick={buttonVal} value={pageNumber + 3}>{pageNumber + 3}</button>
            <button onClick={buttonVal} value={pageNumber + 4}>{pageNumber + 4}</button>
            </>

        )
    }

    const lowerPages = () => {
        return (
        <>
            <button onClick={(e) => {
                buttonVal(e);
            }} value={1}>{1}</button>
            <button onClick={(e) => {
                buttonVal(e);
            }} value={2}>{2}</button>
            <button onClick={buttonVal} value={3}>{3}</button>
            <button onClick={buttonVal} value={4}>{4}</button>
            <button onClick={buttonVal} value={5}>{5}</button>


        </>
        )
    }

    if (loading) {
        return (
            <div>Loading...</div>
        )
    }


    return (

        <div>

            {character.map(data => (
                <div className="character-details" key={data.url}>
                    <button className="character-details"
                        onClick={() => navigateToCharacter(data.url)}>
                        {data.name === "" ? data.aliases : data.name}
                        {data.culture === '' ? '' : `, ${data.culture}`},
                        {data.gender === 'Female' ? ' ♀️' : ' ♂️'}
                    </button>


                </div>


            ))}
            <button onClick={prevPage} >Prev</button>
                {pageNumber >= 5 ? hiPages() : lowerPages()}

            {/* <button style={{backgroundColor: 'grey'}} onClick={buttonVal} value={pageNumber}>{pageNumber}</button>
            <button onClick={buttonVal} value={pageNumber + 1}>{pageNumber + 1}</button>
            <button onClick={buttonVal} value={pageNumber + 2}>{pageNumber + 2}</button>
            <button onClick={buttonVal} value={pageNumber + 3}>{pageNumber + 3}</button>
            <button onClick={buttonVal} value={pageNumber + 4}>{pageNumber + 4}</button>
             */}

            <button onClick={nextPage}>Next</button>



        </div>
    )
}

export default Characters;

